import NotificationsIcon from '@mui/icons-material/Notifications';
import ChatBubbleOutlineIcon from '@mui/icons-material/ChatBubbleOutline';
import EventIcon from '@mui/icons-material/Event';
import FlashOnIcon from '@mui/icons-material/FlashOn';
import AccountBalanceWalletIcon from '@mui/icons-material/AccountBalanceWallet';
import SchoolIcon from '@mui/icons-material/School';
import { styled } from '@mui/material/styles';
import { useNavigate } from 'react-router-dom';
import { useStudent } from '../contexts/StudentContext';
import { NotificationContext } from '../contexts/NotificationContext'; // Import Notification Context
import { Fade, Slide } from '@mui/material'; // Import Fade and Slide animations

const Dashboard = () => {
  const navigate = useNavigate();
  const { currentStudent } = useStudent(); // Use context to get the current student
  const { sendNotification, alerts } = useContext(NotificationContext); // Use NotificationContext to send notifications and access alerts
  const [student, setStudent] = useState(null);
  const [error, setError] = useState(null); // Track if there's an error loading student data
  const [quizHistory, setQuizHistory] = useState([]); // State to store quiz history from localStorage

  useEffect(() => {
    // Load student data dynamically based on the current student's ID
    const loadStudentData = async () => {
      if (currentStudent && currentStudent.id) {
        try {
          console.log(`Loading student data for: ${currentStudent.id}`);
          const data = await import(`../data/students/${currentStudent.id}.js`); // Dynamically import student file
          setStudent(data.default); // Set the student data
        } catch (err) {
          console.error('Error loading student data:', err);
          setError('Failed to load student data.');
        }
      } else {
        setError('No current student data available.');
      }
    };

    loadStudentData();

    // Load quiz history from localStorage
    const storedQuizHistory = JSON.parse(localStorage.getItem('quizHistory')) || [];
    setQuizHistory(storedQuizHistory);

  }, [currentStudent]);

  useEffect(() => {
    if (student) {
      scheduleSessionReminders(); // Schedule the reminders only if student data is available
    }
  }, [student, upcomingSessions]); // Ensure it's called when student data and sessions are updated

  // Helper functions for reminders
  const getLastSessionOfMonth = () => {
    return tutoringHistory.reduce((lastSession, session) => {
      const sessionDate = new Date(session.date);
      if (sessionDate.getMonth() === currentDate.getMonth() && sessionDate > lastSession) {
        return sessionDate;
      }
      return lastSession;
    }, new Date(0)); // Initialize with a very early date
  };

  const getFirstSessionOfMonth = () => {
    return tutoringHistory.reduce((firstSession, session) => {
      const sessionDate = new Date(session.date);
      if (sessionDate.getMonth() === currentDate.getMonth() && sessionDate < firstSession) {
        return sessionDate;
      }
      return firstSession;
    }, new Date(9999, 11, 31)); // Initialize with a very late date
  };

  const scheduleSessionReminders = () => {
    // 1. Sessions ending soon (remind 25% before end)
    upcomingSessions.forEach((session) => {
      const sessionDateTime = new Date(`${session.date}T${session.time || '00:00:00'}`);
      const sessionDuration = session.duration || 60 * 60 * 1000; // Assume 1-hour session if duration is not provided
      const sessionEndTime = sessionDateTime.getTime() + sessionDuration;

      // Calculate the point when 25% of the session time is remaining
      const reminderTime = sessionEndTime - 0.25 * sessionDuration;
      const now = new Date();
      const timeUntilReminder = reminderTime - now.getTime();

      if (timeUntilReminder > 0) {
        setTimeout(() => {
          const endTimeText = new Date(sessionEndTime).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
          sendNotification('Session Ending Soon', `Your session for ${session.subject} will end at ${endTimeText}. Only 25% of the session time remains.`);
        }, timeUntilReminder);
      }
    });

    // 2. Notify when the last session of the month happens
    const lastSessionOfMonth = getLastSessionOfMonth();
    if (lastSessionOfMonth) {
      const timeUntilLastSession = new Date(lastSessionOfMonth) - currentDate;
      if (timeUntilLastSession > 0) {
        setTimeout(() => {
          sendNotification('Last Session of the Month', `Your last session this month is on ${lastSessionOfMonth.toLocaleDateString()}.`);
        }, timeUntilLastSession);
      }
    }

    // 3. Notify when a new session for the month begins
    const firstSessionOfMonth = getFirstSessionOfMonth();
    if (firstSessionOfMonth) {
      const timeUntilFirstSession = new Date(firstSessionOfMonth) - currentDate;
      if (timeUntilFirstSession > 0) {
        setTimeout(() => {
          sendNotification('New Session Starting', `Your first session this month starts on ${firstSessionOfMonth.toLocaleDateString()}.`);
        }, timeUntilFirstSession);
      }
    }

    // 4. Remind students to take quizzes
    setTimeout(() => {
      sendNotification('Quiz Reminder', 'Don’t forget to take your quizzes to sharpen your mind!');
    }, 5 * 60 * 1000); // Example: Remind after 5 minutes of page load
  };

  const handleQuizzesClick = () => {
    navigate('/ai-quiz');
  };
  const handleStudentProfileClick = () => {
    navigate('/student-profile');
  };

  const handleFlashcardsClick = () => {
    navigate('/flashcards');
  };

  const handlePaymentSessionClick = () => {
    navigate('/payment-session');
  };

  if (error) {
    return <Typography sx={{ color: '#ffffff', textAlign: 'center', marginTop: '20px' }}>{error}</Typography>;
  }

  // If student data is still loading, show a fun and modern loader
  if (!student) {
    return (
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          height: '100vh',
          backgroundColor: '#2c2c2c',
          color: '#fff',
        }}
      >
        Loading...
      </Box>
    );
  }

  return (
    <Box
      component="main"
      sx={{
        flexGrow: 1,
        p: 3,
        ml: { xs: 0, md: '240px' }, // Add margin-left on larger screens to make space for the sidebar
        backgroundColor: '#2c2c2c',
        color: '#ffffff',
        minHeight: '100vh',
      }}
    >
      <Grid container spacing={2}>
        {/* Welcome Message */}
        <Grid item xs={12}>
          <Fade in={true} timeout={1000}>
            <Box display="flex" justifyContent="space-between" alignItems="center">
              <Typography variant="h5" sx={{ color: '#ffffff' }}>
                {personalDetails?.name ? `Hello, ${personalDetails.name}!` : 'Hello, Student!'}
              </Typography>
              <Box sx={{ position: 'relative', display: 'flex', alignItems: 'center' }}>
                <IconButton color="primary">
                  <Badge
                    badgeContent={alerts.length}
                    color="error"
                    sx={{
                      position: 'absolute',
                      top: 0,
                      right: 0,
                    }}
                  >
                    <NotificationsIcon sx={{ color: 'teal' }} />
                  </Badge>
                </IconButton>
                <IconButton onClick={handleStudentProfileClick}>
                  <Avatar sx={{ cursor: 'pointer', backgroundColor: 'teal', color: '#fff', width: 48, height: 48 }}>
                    {firstLetter}
                  </Avatar>
                </IconButton>
              </Box>
            </Box>
          </Fade>
        </Grid>

        {/* Balance and Session Details */}
        <Grid item xs={12}>
          <Slide direction="up" in={true} timeout={1000}>
            <StyledPaper>
              <Typography
                variant="body1"
                sx={{
                  display: 'flex',
                  alignItems: 'center',
                  color: '#ffffff',
                  '&:hover': { color: 'teal', cursor: 'pointer' },
                }}
                onClick={handlePaymentSessionClick}
              >
                <AccountBalanceWalletIcon sx={{ mr: 1, color: hasActiveSession ? 'teal' : 'darkgray' }} />
                Your Balance: {balance !== 'N/A' ? `R${balance}` : '00'}
              </Typography>
              <Typography variant="body2" sx={{ color: '#ffffff', mt: 1 }}>
                Session Type: {sessionType}
              </Typography>
              <Typography variant="body2" sx={{ color: '#ffffff' }}>
                Start Date: {startDate}
              </Typography>
              <Typography variant="body2" sx={{ color: '#ffffff' }}>
                End Date: {endDateDisplay}
              </Typography>
              {!hasActiveSession && (
                <Typography variant="body2" sx={{ color: 'red', mt: 1 }}>
                  {noActiveSessionMessage}
                </Typography>
              )}
            </StyledPaper>
          </Slide>
        </Grid>

        {/* Active Progress Section */}
        <Grid item xs={12} md={6}>
          <Slide direction="left" in={true} timeout={800}>
            <StyledPaper>
              <Typography variant="body1" sx={{ display: 'flex', alignItems: 'center', color: '#ffffff' }}>
                <FlashOnIcon sx={{ mr: 1, color: 'teal' }} /> Active Progress
              </Typography>
              <List>
                <ListItem onClick={handleFlashcardsClick} sx={{ cursor: 'pointer' }}>
                  <ListItemIcon>
                    <FlashOnIcon sx={{ color: 'teal' }} />
                  </ListItemIcon>
                  <ListItemText primary="Flashcards: 0% Completed" primaryTypographyProps={{ color: '#ffffff' }} />
                </ListItem>
                <ListItem onClick={handleQuizzesClick} sx={{ cursor: 'pointer' }}>
                  <ListItemIcon>
                    <SchoolIcon sx={{ color: 'teal' }} />
                  </ListItemIcon>
                  <ListItemText primary={`Quizzes: ${totalPercentage}% Total Pass Rate`} primaryTypographyProps={{ color: '#ffffff' }} />
                </ListItem>
              </List>
            </StyledPaper>
          </Slide>
        </Grid>

        {/* Upcoming Sessions Section */}
        <Grid item xs={12} md={6}>
          <Slide direction="right" in={true} timeout={800}>
            <StyledPaper>
              <Typography variant="body1" sx={{ display: 'flex', alignItems: 'center', color: '#ffffff' }}>
                <EventIcon sx={{ mr: 1, color: 'teal' }} /> Upcoming Sessions
              </Typography>
              <List>
                {upcomingSessions.length > 0 ? (
                  upcomingSessions.map((session, index) => (
                    <ListItem key={index}>
                      <ListItemText
                        primary={`${session.subject} - ${session.topic}`}
                        secondary={`Date: ${new Date(session.date).toLocaleDateString()}`}
                        primaryTypographyProps={{ color: '#ffffff', fontSize: '0.9rem' }}
                        secondaryTypographyProps={{ color: '#ffffff', fontSize: '0.8rem' }}
                      />
                    </ListItem>
                  ))
                ) : (
                  <Typography sx={{ color: '#ffffff' }}>No upcoming sessions.</Typography>
                )}
              </List>
            </StyledPaper>
          </Slide>
        </Grid>

        {/* Messaging Section */}
        <Grid item xs={12}>
          <Slide direction="up" in={true} timeout={1000}>
            <StyledPaper>
              <Typography variant="body1" sx={{ display: 'flex', alignItems: 'center', color: '#ffffff' }}>
                <ChatBubbleOutlineIcon sx={{ mr: 1, color: 'teal' }} /> Messages
              </Typography>
              <List>
                <ListItem>
                  <ListItemText
                    primary="John Doe"
                    secondary="Hi, can you help me with my assignment?"
                    primaryTypographyProps={{ color: '#ffffff', fontSize: '0.9rem' }}
                    secondaryTypographyProps={{ color: '#ffffff', fontSize: '0.8rem' }}
                  />
                </ListItem>
                <ListItem>
                  <ListItemText
                    primary="Tutor Anna"
                    secondary="Don't forget your session tomorrow at 10 AM!"
                    primaryTypographyProps={{ color: '#ffffff', fontSize: '0.9rem' }}
                    secondaryTypographyProps={{ color: '#ffffff', fontSize: '0.8rem' }}
                  />
                </ListItem>
              </List>
            </StyledPaper>
          </Slide>
        </Grid>

        {/* Additional Section: Alerts or Reminders */}
        <Grid item xs={12}>
          <Slide direction="up" in={true} timeout={1000}>
            <StyledPaper>
              <Typography variant="body1" sx={{ display: 'flex', alignItems: 'center', color: '#ffffff' }}>
                <NotificationsIcon sx={{ mr: 1, color: 'teal' }} /> Alerts & Reminders
              </Typography>
              <List>
                {alerts.length > 0 ? (
                  alerts.map((alert, index) => (
                    <ListItem key={index}>
                      <ListItemText
                        primary={alert.title}
                        secondary={alert.body}
                        primaryTypographyProps={{ color: '#ffffff', fontSize: '0.9rem' }}
                      />
                    </ListItem>
                  ))
                ) : (
                  <Typography sx={{ color: '#ffffff' }}>No alerts at the moment.</Typography>
                )}
              </List>
            </StyledPaper>
          </Slide>
        </Grid>
      </Grid>
    </Box>
  );
};

// Styled components and Loader definition
const StyledPaper = styled(Paper)({
  padding: '20px',
  marginBottom: '20px',
  backgroundColor: '#333',
  color: '#ffffff',
  borderRadius: '8px',
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
});

export default Dashboard;
