// src/components/StudyMaterials.js
import React from 'react';
import { Box, Grid, Typography, Paper } from '@mui/material';
import { styled } from '@mui/material/styles';

const StudyMaterials = () => {
  return (
    <Box
      component="main"
      sx={{
        flexGrow: 1,
        p: 3,
        backgroundColor: '#2c2c2c',
        color: '#fff',
        minHeight: '100vh',
      }}
    >
      <Grid container spacing={2}>
        <Grid item xs={12}>
          <Box display="flex" justifyContent="space-between" alignItems="center">
            <Typography variant="h4">Study Materials</Typography>
          </Box>
        </Grid>

        <Grid item xs={12}>
          <StyledPaper>
            <Typography variant="body1" sx={{ mb: 2 }}>
              Access your study materials here. More features coming soon!
            </Typography>
            <Grid container spacing={2}>
              <Grid item xs={12} sm={6} md={4}>
                <FeatureCard title="Notes" description="Organize your study notes effectively." />
              </Grid>
              <Grid item xs={12} sm={6} md={4}>
                <FeatureCard title="AI Quiz" description="Test your knowledge with AI-generated quizzes." />
              </Grid>
              <Grid item xs={12} sm={6} md={4}>
                <FeatureCard title="Past Papers" description="Access past exam papers for better preparation." />
              </Grid>
              <Grid item xs={12} sm={6} md={4}>
                <FeatureCard title="More" description="Additional study resources coming soon." />
              </Grid>
            </Grid>
          </StyledPaper>
        </Grid>
      </Grid>
    </Box>
  );
};

const StyledPaper = styled(Paper)({
  padding: '20px',
  marginBottom: '20px',
  backgroundColor: '#333',
  color: '#fff',
  borderRadius: '8px',
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
});

const FeatureCard = ({ title, description }) => {
  return (
    <StyledFeatureCard>
      <Typography variant="h6" sx={{ mb: 1 }}>
        {title}
      </Typography>
      <Typography variant="body2">{description}</Typography>
      <ComingSoonBadge>Coming Soon</ComingSoonBadge>
    </StyledFeatureCard>
  );
};

const StyledFeatureCard = styled(Paper)({
  padding: '15px',
  backgroundColor: '#444',
  color: '#fff',
  borderRadius: '8px',
  textAlign: 'center',
  position: 'relative',
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
  overflow: 'hidden',
  '&:hover': {
    boxShadow: '0 6px 12px rgba(0, 0, 0, 0.3)',
  },
});

const ComingSoonBadge = styled('div')({
  position: 'absolute',
  top: 10,
  right: 10,
  backgroundColor: '#e91e63',
  color: '#fff',
  padding: '5px 10px',
  borderRadius: '5px',
  fontSize: '0.75rem',
});

export default StudyMaterials;
