import React, { useEffect, useState } from 'react';
import {
  Box,
  Grid,
  Paper,
  Typography,
  Avatar,
  IconButton,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Badge,
} from '@mui/material';
import NotificationsIcon from '@mui/icons-material/Notifications';
import ChatBubbleOutlineIcon from '@mui/icons-material/ChatBubbleOutline';
import EventIcon from '@mui/icons-material/Event';
import FlashOnIcon from '@mui/icons-material/FlashOn';
import AccountBalanceWalletIcon from '@mui/icons-material/AccountBalanceWallet';
import SchoolIcon from '@mui/icons-material/School';
import { styled } from '@mui/material/styles';
import { useNavigate } from 'react-router-dom';
import { useStudent } from '../contexts/StudentContext'; // Import the custom hook

const Dashboard = () => {
  const navigate = useNavigate();
  const { currentStudent } = useStudent(); // Use context to get the current student
  const [student, setStudent] = useState(null);
  const [error, setError] = useState(null); // Track if there's an error loading student data

  useEffect(() => {
    // Load student data dynamically based on the current student's ID
    const loadStudentData = async () => {
      if (currentStudent && currentStudent.id) {
        try {
          console.log(`Loading student data for: ${currentStudent.id}`);
          const data = await import(`../data/students/${currentStudent.id}.js`); // Dynamically import student file
          setStudent(data.default); // Set the student data
        } catch (err) {
          console.error('Error loading student data:', err);
          setError('Failed to load student data.');
        }
      } else {
        setError('No current student data available.');
      }
    };

    loadStudentData();
  }, [currentStudent]);

  // Show an error message if there's a problem loading the student data
  if (error) {
    return <Typography sx={{ color: '#ffffff', textAlign: 'center', marginTop: '20px' }}>{error}</Typography>;
  }

  // If student data is still loading, show a fun and modern loader
  if (!student) {
    return (
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          height: '100vh',
          backgroundColor: '#2c2c2c',
          color: '#fff',
        }}
      >
        <Loader>
          <div className="dot"></div>
          <div className="dot"></div>
          <div className="dot"></div>
        </Loader>
      </Box>
    );
  }

  // Deconstruct data from the student object
  const { personalDetails, paymentInformation = [], tutoringHistory = [], quizHistory = [] } = student;

  // Safeguard if paymentInformation or tutoringHistory is missing
  const latestPayment = paymentInformation.length > 0 ? paymentInformation[paymentInformation.length - 1] : null;
  const balance = latestPayment ? latestPayment.amountPaid : 'N/A';
  const sessionsCovered = latestPayment ? latestPayment.sessionsCovered : [];

  const latestSession = sessionsCovered.length > 0 ? sessionsCovered[0] : {};
  const sessionType = latestSession.subject || 'N/A';
  const startDate = latestSession.startDate || 'N/A';
  const endDate = latestSession.endDate || 'N/A';

  const totalCorrect = quizHistory.reduce((acc, quiz) => acc + quiz.score, 0);
  const totalQuestions = quizHistory.reduce((acc, quiz) => acc + quiz.numQuestions, 0);
  const totalPercentage = totalQuestions > 0 ? ((totalCorrect / totalQuestions) * 100).toFixed(2) : 'N/A';

  const upcomingSessions = tutoringHistory
    .filter((session) => new Date(session.date) > new Date())
    .slice(0, 4);

  const handleQuizzesClick = () => {
    navigate('/ai-quiz');
  };

  const handleFlashcardsClick = () => {
    navigate('/flashcards');
  };

  const handlePaymentSessionClick = () => {
    navigate('/payment-session');
  };

  return (
    <Box
      component="main"
      sx={{
        flexGrow: 1,
        p: 3,
        ml: { xs: 0, md: '240px' }, // Add margin-left on larger screens to make space for the sidebar
        backgroundColor: '#2c2c2c',
        color: '#ffffff',
        minHeight: '100vh',
      }}
    >
      <Grid container spacing={2}>
        {/* Welcome Message */}
        <Grid item xs={12}>
          <Box display="flex" justifyContent="space-between" alignItems="center">
            <Typography variant="h5" sx={{ color: '#ffffff' }}> {/* Reduced from h4 to h5 */}
              {personalDetails?.name ? `Hello, ${personalDetails.name}!` : 'Hello, Student!'}
            </Typography>
            <Box>
              <IconButton color="primary">
                <Badge badgeContent={4} color="error">
                  <NotificationsIcon sx={{ color: 'teal' }} />
                </Badge>
              </IconButton>
              <Avatar alt="Student Profile" src="/static/images/avatar/1.jpg" />
            </Box>
          </Box>
        </Grid>

        {/* Balance and Session Details */}
        <Grid item xs={12}>
          <StyledPaper>
            <Typography
              variant="body1" // Reduced from h6 to body1
              sx={{
                display: 'flex',
                alignItems: 'center',
                color: '#ffffff',
                '&:hover': { color: 'teal', cursor: 'pointer' },
              }}
              onClick={handlePaymentSessionClick}
            >
              <AccountBalanceWalletIcon sx={{ mr: 1, color: 'teal' }} /> Your Balance: {balance !== 'N/A' ? `R${balance}` : 'N/A'}
            </Typography>
            <Typography variant="body2" sx={{ color: '#ffffff', mt: 1 }}> {/* Smaller font */}
              Session Type: {sessionType}
            </Typography>
            <Typography variant="body2" sx={{ color: '#ffffff' }}> {/* Smaller font */}
              Start Date: {startDate}
            </Typography>
            <Typography variant="body2" sx={{ color: '#ffffff' }}> {/* Smaller font */}
              End Date: {endDate}
            </Typography>
          </StyledPaper>
        </Grid>

        {/* Active Progress Section */}
        <Grid item xs={12} md={6}>
          <StyledPaper>
            <Typography variant="body1" sx={{ display: 'flex', alignItems: 'center', color: '#ffffff' }}>
              <FlashOnIcon sx={{ mr: 1, color: 'teal' }} /> Active Progress
            </Typography>
            <List>
              <ListItem onClick={handleFlashcardsClick} sx={{ cursor: 'pointer' }}> {/* Flashcards click handler */}
                <ListItemIcon>
                  <FlashOnIcon sx={{ color: 'teal' }} />
                </ListItemIcon>
                <ListItemText primary="Flashcards: 0% Completed" primaryTypographyProps={{ color: '#ffffff' }} />
              </ListItem>
              <ListItem onClick={handleQuizzesClick} sx={{ cursor: 'pointer' }}> {/* Quizzes click handler */}
                <ListItemIcon>
                  <SchoolIcon sx={{ color: 'teal' }} />
                </ListItemIcon>
                <ListItemText primary={`Quizzes: ${totalPercentage}% Total Pass Rate`} primaryTypographyProps={{ color: '#ffffff' }} />
              </ListItem>
            </List>
          </StyledPaper>
        </Grid>
        
        {/* Upcoming Sessions Section */}
        <Grid item xs={12} md={6}>
          <StyledPaper>
            <Typography variant="body1" sx={{ display: 'flex', alignItems: 'center', color: '#ffffff' }}>
              <EventIcon sx={{ mr: 1, color: 'teal' }} /> Upcoming Sessions
            </Typography>
            <List>
              {upcomingSessions.length > 0 ? (
                upcomingSessions.map((session, index) => (
                  <ListItem key={index}>
                    <ListItemText
                      primary={`${session.subject} - ${session.topic}`}
                      secondary={`Date: ${new Date(session.date).toLocaleDateString()}`}
                      primaryTypographyProps={{ color: '#ffffff', fontSize: '0.9rem' }} // Reduced font size
                      secondaryTypographyProps={{ color: '#ffffff', fontSize: '0.8rem' }} // Reduced font size
                    />
                  </ListItem>
                ))
              ) : (
                <Typography sx={{ color: '#ffffff' }}>No upcoming sessions.</Typography>
              )}
            </List>
          </StyledPaper>
        </Grid>

        {/* Messaging Section */}
        <Grid item xs={12}>
          <StyledPaper>
            <Typography variant="body1" sx={{ display: 'flex', alignItems: 'center', color: '#ffffff' }}>
              <ChatBubbleOutlineIcon sx={{ mr: 1, color: 'teal' }} /> Messages
            </Typography>
            <List>
              <ListItem>
                <ListItemText
                  primary="John Doe"
                  secondary="Hi, can you help me with my assignment?"
                  primaryTypographyProps={{ color: '#ffffff', fontSize: '0.9rem' }} // Reduced font size
                  secondaryTypographyProps={{ color: '#ffffff', fontSize: '0.8rem' }} // Reduced font size
                />
              </ListItem>
              <ListItem>
                <ListItemText
                  primary="Tutor Anna"
                  secondary="Don't forget your session tomorrow at 10 AM!"
                  primaryTypographyProps={{ color: '#ffffff', fontSize: '0.9rem' }} // Reduced font size
                  secondaryTypographyProps={{ color: '#ffffff', fontSize: '0.8rem' }} // Reduced font size
                />
              </ListItem>
            </List>
          </StyledPaper>
        </Grid>

        {/* Additional Section: Alerts or Reminders */}
        <Grid item xs={12}>
          <StyledPaper>
            <Typography variant="body1" sx={{ display: 'flex', alignItems: 'center', color: '#ffffff' }}>
              <NotificationsIcon sx={{ mr: 1, color: 'teal' }} /> Alerts & Reminders
            </Typography>
            <List>
              <ListItem>
                <ListItemText
                  primary="Assignment due: History Essay - Sep 12"
                  primaryTypographyProps={{ color: '#ffffff', fontSize: '0.9rem' }} // Reduced font size
                />
              </ListItem>
              <ListItem>
                <ListItemText
                  primary="New Quiz Available: Science Chapter 4"
                  primaryTypographyProps={{ color: '#ffffff', fontSize: '0.9rem' }} // Reduced font size
                />
              </ListItem>
            </List>
          </StyledPaper>
        </Grid>
      </Grid>
    </Box>
  );
};

// Fun bouncing dots loader
const Loader = styled('div')({
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  '& .dot': {
    width: '12px',
    height: '12px',
    margin: '0 5px',
    borderRadius: '50%',
    backgroundColor: 'teal',
    animation: 'bounce 0.6s infinite alternate',
  },
  '& .dot:nth-of-type(2)': {
    animationDelay: '0.2s',
  },
  '& .dot:nth-of-type(3)': {
    animationDelay: '0.4s',
  },
  '@keyframes bounce': {
    to: {
      transform: 'translateY(-50%)',
      opacity: 0.5,
    },
  },
});

const StyledPaper = styled(Paper)({
  padding: '20px',
  marginBottom: '20px',
  backgroundColor: '#333',
  color: '#ffffff',
  borderRadius: '8px',
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
});

export default Dashboard;
