// src/components/UpcomingSessions.js
import React, { useState } from 'react';
import { Box, Grid, Typography, Paper, List, ListItem, ListItemText, Divider } from '@mui/material';
import { styled } from '@mui/material/styles';

const UpcomingSessions = () => {
  // Dummy data for upcoming sessions
  const [upcomingSessions] = useState([
    { id: 1, date: '2024-09-18', subject: 'Mathematics' },
    { id: 2, date: '2024-09-20', subject: 'Physics' },
    { id: 3, date: '2024-09-22', subject: 'Biology' },
    { id: 4, date: '2024-09-24', subject: 'Chemistry' },
    { id: 5, date: '2024-09-26', subject: 'English' },
  ]);

  // Calculate total sessions and sessions left
  const totalSessions = upcomingSessions.length;
  const sessionsLeft = upcomingSessions.filter(session => new Date(session.date) > new Date()).length;

  return (
    <Box component="main" sx={{ flexGrow: 1, p: 3, backgroundColor: '#2c2c2c', color: '#fff', minHeight: '100vh' }}>
      <Grid container spacing={2}>
        <Grid item xs={12}>
          <Typography variant="h4" sx={{ fontSize: '2rem', fontWeight: 'bold' }}>Upcoming Sessions</Typography>
        </Grid>

        {/* Summary Box */}
        <Grid item xs={12} md={6}>
          <SummaryPaper>
            <Typography variant="h6" sx={{ fontWeight: 'bold', color: 'teal' }}>Sessions Overview</Typography>
            <Typography variant="body1" sx={{ fontSize: '1.2rem' }}>
              Total Sessions This Month: {totalSessions}
            </Typography>
            <Typography variant="body1" sx={{ fontSize: '1.2rem' }}>
              Sessions Left: {sessionsLeft}
            </Typography>
          </SummaryPaper>
        </Grid>

        {/* Upcoming Sessions List */}
        <Grid item xs={12} md={6}>
          <SessionListPaper>
            <Typography variant="h6" sx={{ fontWeight: 'bold', color: 'teal' }}>Upcoming Sessions</Typography>
            <List>
              {upcomingSessions.map((session) => (
                <React.Fragment key={session.id}>
                  <ListItem>
                    <ListItemText
                      primary={session.subject}
                      secondary={`Date: ${session.date}`}
                      primaryTypographyProps={{ style: { color: '#fff', fontSize: '1.1rem', fontWeight: 'bold' } }}
                      secondaryTypographyProps={{ style: { color: '#ccc' } }}
                    />
                  </ListItem>
                  <Divider sx={{ backgroundColor: '#555' }} />
                </React.Fragment>
              ))}
            </List>
          </SessionListPaper>
        </Grid>
      </Grid>
    </Box>
  );
};

const SummaryPaper = styled(Paper)({
  padding: '20px',
  backgroundColor: '#333',
  color: '#fff',
  borderRadius: '8px',
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
  textAlign: 'center',
});

const SessionListPaper = styled(Paper)({
  padding: '20px',
  backgroundColor: '#333',
  color: '#fff',
  borderRadius: '8px',
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
});

export default UpcomingSessions;
