// src/components/PasswordReset.js
import React, { useState } from 'react';
import { Box, Button, TextField, Typography, Paper } from '@mui/material';
import studentData from '../data/studentData'; // Importing student data

const PasswordReset = () => {
  const [studentId, setStudentId] = useState('');
  const [dateOfBirth, setDateOfBirth] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleResetPassword = () => {
    // Find the student by ID
    const student = studentData.find(
      (student) => student.id === studentId && student.dateOfBirth === dateOfBirth
    );

    if (!student) {
      setMessage('Student ID or Date of Birth is incorrect.');
      return;
    }

    // Check if the new password matches the confirmation
    if (newPassword !== confirmPassword) {
      setMessage('Passwords do not match.');
      return;
    }

    // Update the password
    student.password = newPassword;
    setMessage('Password updated successfully.');
  };

  return (
    <Box display="flex" justifyContent="center" alignItems="center" height="100vh">
      <Paper sx={{ padding: '30px', width: '400px' }}>
        <Typography variant="h5" sx={{ marginBottom: '20px' }}>Reset Password</Typography>
        <TextField
          label="Student ID"
          fullWidth
          value={studentId}
          onChange={(e) => setStudentId(e.target.value)}
          sx={{ marginBottom: '15px' }}
        />
        <TextField
          label="Date of Birth"
          type="date"
          fullWidth
          value={dateOfBirth}
          onChange={(e) => setDateOfBirth(e.target.value)}
          InputLabelProps={{ shrink: true }}
          sx={{ marginBottom: '15px' }}
        />
        <TextField
          label="New Password"
          type="password"
          fullWidth
          value={newPassword}
          onChange={(e) => setNewPassword(e.target.value)}
          sx={{ marginBottom: '15px' }}
        />
        <TextField
          label="Confirm New Password"
          type="password"
          fullWidth
          value={confirmPassword}
          onChange={(e) => setConfirmPassword(e.target.value)}
          sx={{ marginBottom: '15px' }}
        />
        <Button variant="contained" color="primary" onClick={handleResetPassword} fullWidth>
          Reset Password
        </Button>
        {message && <Typography sx={{ marginTop: '15px', color: message.includes('successfully') ? 'green' : 'red' }}>{message}</Typography>}
      </Paper>
    </Box>
  );
};

export default PasswordReset;
