// src/components/AIQuiz.js
import React, { useState } from 'react';
import { Box, Typography, Button, TextField, CircularProgress } from '@mui/material';
import axios from 'axios';

const AIQuiz = () => {
  const [topic, setTopic] = useState('');
  const [difficulty, setDifficulty] = useState('');
  const [numberOfQuestions, setNumberOfQuestions] = useState(5);
  const [quiz, setQuiz] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleGenerateQuiz = async () => {
    setLoading(true);
    setQuiz(null); // Clear previous quiz
    try {
      const response = await axios.post('http://localhost:5000/api/generate-quiz', {
        topic,
        difficulty,
        numberOfQuestions,
      });
      setQuiz(response.data.quiz);
    } catch (error) {
      console.error('Error generating quiz:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Box
      sx={{
        flexGrow: 1,
        p: 3,
        backgroundColor: '#2c2c2c',
        color: '#fff',
        minHeight: '100vh',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
      }}
    >
      <Typography variant="h4" gutterBottom>
        AI Quiz
      </Typography>
      <Typography variant="body1" sx={{ mb: 2 }}>
        Test your knowledge with our AI-generated quizzes!
      </Typography>
      <TextField
        label="Topic"
        variant="outlined"
        value={topic}
        onChange={(e) => setTopic(e.target.value)}
        sx={{ mb: 2, backgroundColor: '#fff' }}
      />
      <TextField
        label="Difficulty"
        variant="outlined"
        value={difficulty}
        onChange={(e) => setDifficulty(e.target.value)}
        sx={{ mb: 2, backgroundColor: '#fff' }}
      />
      <TextField
        label="Number of Questions"
        type="number"
        variant="outlined"
        value={numberOfQuestions}
        onChange={(e) => setNumberOfQuestions(Number(e.target.value))}
        sx={{ mb: 2, backgroundColor: '#fff' }}
      />
      <Button variant="contained" color="primary" onClick={handleGenerateQuiz} disabled={loading}>
        {loading ? <CircularProgress size={24} /> : 'Start Quiz'}
      </Button>

      {quiz && (
        <Box sx={{ mt: 4, p: 2, border: '1px solid #fff', borderRadius: '8px', width: '100%', maxWidth: 600 }}>
          <Typography variant="h5" gutterBottom>
            Generated Quiz
          </Typography>
          <Typography variant="body1" sx={{ whiteSpace: 'pre-wrap' }}>
            {quiz}
          </Typography>
        </Box>
      )}
    </Box>
  );
};

export default AIQuiz;
