// src/components/FormulaSheets.js
import React, { useState } from 'react';
import { Box, Typography, Button, Collapse } from '@mui/material';
import { MathJax, MathJaxContext } from 'better-react-mathjax'; // Import MathJax
import formulasData from '../data/formulas.json'; // Import the JSON file

const FormulaSheets = () => {
  const [selectedFormula, setSelectedFormula] = useState(null);

  // Toggle the selected formula when clicking on its name
  const handleFormulaClick = (formulaName) => {
    setSelectedFormula(selectedFormula === formulaName ? null : formulaName);
  };

  return (
    <MathJaxContext>
      <Box
        sx={{
          flexGrow: 1,
          p: 3,
          backgroundColor: '#2c2c2c',
          color: '#fff',
          minHeight: '100vh',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
        }}
      >
        <Typography variant="h4" gutterBottom>
          Formula Dictionary
        </Typography>

        {/* Display Formula Names and Expandable Info */}
        {formulasData.formulas.map((formula, index) => (
          <Box
            key={index}
            sx={{
              width: '100%',
              maxWidth: 600,
              mb: 2,
              border: '1px solid #008080', // Teal border color
              borderRadius: 2,
              backgroundColor: '#1e1e1e', // Dark background for the formula box
            }}
          >
            <Button
              fullWidth
              sx={{
                color: '#008080', // Teal text for the formula name
                justifyContent: 'flex-start',
                textTransform: 'none',
                fontSize: '1.1rem',
                backgroundColor: '#1e1e1e', // Dark background for the button
                '&:hover': {
                  backgroundColor: '#333333', // Slightly lighter on hover
                },
              }}
              onClick={() => handleFormulaClick(formula.name)}
            >
              {formula.name}
            </Button>
            <Collapse in={selectedFormula === formula.name}>
              <Box sx={{ p: 2, color: '#ffffff' }}>
                <Typography variant="body1">
                  Formula: <MathJax>{`\\( ${formula.formula} \\)`}</MathJax>
                </Typography>
                <Typography variant="body1" sx={{ mt: 1 }}>
                  <strong>Breakdown:</strong>
                  {Object.entries(formula.breakdown).map(([key, value]) => (
                    <Typography key={key}>
                      <strong>{key}:</strong> {value}
                    </Typography>
                  ))}
                </Typography>
                <Typography variant="body1" sx={{ mt: 1 }}>
                  <strong>Example Problem:</strong> {formula.example.problem}
                </Typography>
                <Typography variant="body1">
                  <strong>Solution:</strong>
                  {formula.example.solution.map((step, i) => (
                    <Typography key={i}>{step}</Typography>
                  ))}
                </Typography>
              </Box>
            </Collapse>
          </Box>
        ))}
      </Box>
    </MathJaxContext>
  );
};

export default FormulaSheets;
