// src/components/FormulaSheets.js
import React, { useState, useEffect } from 'react';
import { Box, Typography, Button, Select, MenuItem, FormControl, InputLabel } from '@mui/material';
import formulasData from '../data/formulas.json'; // Import the JSON file

const FormulaSheets = () => {
  const [selectedSubject, setSelectedSubject] = useState('');
  const [selectedGrade, setSelectedGrade] = useState('');
  const [selectedTopic, setSelectedTopic] = useState('');
  const [currentFormulas, setCurrentFormulas] = useState([]);

  useEffect(() => {
    if (selectedSubject && selectedGrade && selectedTopic) {
      const formulas = [];

      // Loop through subjects to match the selected subject
      formulasData.subjects.forEach((subject) => {
        if (subject.subject === selectedSubject) {
          subject.grades.forEach((grade) => {
            // Check if the selected grade is in the formula's grades array
            if (grade.grade === selectedGrade || grade.formulas?.some((f) => f.grades.includes(selectedGrade))) {
              grade.topics.forEach((topic) => {
                if (topic.topic === selectedTopic) {
                  formulas.push(...topic.formulas.filter((formula) =>
                    formula.grades.includes(selectedGrade)
                  ));
                }
              });
            }
          });
        }
      });

      setCurrentFormulas(formulas);
    }
  }, [selectedSubject, selectedGrade, selectedTopic]);

  return (
    <Box
      sx={{
        flexGrow: 1,
        p: 3,
        backgroundColor: '#2c2c2c',
        color: '#fff',
        minHeight: '100vh',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
      }}
    >
      <Typography variant="h4" gutterBottom>
        Formula Sheets
      </Typography>
      
      {/* Subject Selector */}
      <FormControl sx={{ mb: 2, minWidth: 200 }}>
        <InputLabel>Subject</InputLabel>
        <Select
          value={selectedSubject}
          onChange={(e) => setSelectedSubject(e.target.value)}
        >
          {formulasData.subjects.map((subject) => (
            <MenuItem key={subject.subject} value={subject.subject}>
              {subject.subject}
            </MenuItem>
          ))}
        </Select>
      </FormControl>

      {/* Grade Selector */}
      {selectedSubject && (
        <FormControl sx={{ mb: 2, minWidth: 200 }}>
          <InputLabel>Grade</InputLabel>
          <Select
            value={selectedGrade}
            onChange={(e) => setSelectedGrade(e.target.value)}
          >
            {formulasData.subjects
              .find((s) => s.subject === selectedSubject)
              .grades.map((grade) => (
                <MenuItem key={grade.grade} value={grade.grade}>
                  {grade.grade}
                </MenuItem>
              ))}
          </Select>
        </FormControl>
      )}

      {/* Topic Selector */}
      {selectedGrade && (
        <FormControl sx={{ mb: 2, minWidth: 200 }}>
          <InputLabel>Topic</InputLabel>
          <Select
            value={selectedTopic}
            onChange={(e) => setSelectedTopic(e.target.value)}
          >
            {formulasData.subjects
              .find((s) => s.subject === selectedSubject)
              .grades.find((g) => g.grade === selectedGrade)
              .topics.map((topic) => (
                <MenuItem key={topic.topic} value={topic.topic}>
                  {topic.topic}
                </MenuItem>
              ))}
          </Select>
        </FormControl>
      )}

      {/* Display Selected Formulas */}
      {currentFormulas.length > 0 && (
        <Box sx={{ mt: 4, width: '100%', maxWidth: 600 }}>
          {currentFormulas.map((formula, index) => (
            <Box key={index} sx={{ mb: 3 }}>
              <Typography variant="h6">{formula.formula}</Typography>
              <Typography variant="body1" sx={{ mb: 1 }}>
                <strong>Breakdown:</strong>
                {Object.entries(formula.breakdown).map(([key, value]) => (
                  <Typography key={key}>
                    <strong>{key}:</strong> {value}
                  </Typography>
                ))}
              </Typography>
              <Typography variant="body1" sx={{ mb: 1 }}>
                <strong>Example Problem:</strong> {formula.example.problem}
              </Typography>
              <Typography variant="body1">
                <strong>Solution:</strong>
                {formula.example.solution.map((step, i) => (
                  <Typography key={i}>{step}</Typography>
                ))}
              </Typography>
            </Box>
          ))}
        </Box>
      )}
    </Box>
  );
};

export default FormulaSheets;
