// src/components/FormulaSheets.js
import React, { useState } from 'react';
import { Box, Typography, Grid, Paper } from '@mui/material';
import { styled } from '@mui/material/styles';
import { MathJax, MathJaxContext } from 'better-react-mathjax'; // Import MathJax
import formulasData from '../data/formulas.json'; // Import the JSON file

const FormulaSheets = () => {
  const [selectedFormula, setSelectedFormula] = useState(null);

  // Toggle the selected formula when clicking on its name
  const handleFormulaClick = (formulaName) => {
    setSelectedFormula(selectedFormula === formulaName ? null : formulaName);
  };

  return (
    <MathJaxContext>
      <Box
        component="main"
        sx={{
          flexGrow: 1,
          p: 3,
          backgroundColor: '#2c2c2c',
          color: '#fff',
          minHeight: '100vh',
        }}
      >
        <Grid container spacing={2}>
          <Grid item xs={12}>
            <Box display="flex" justifyContent="space-between" alignItems="center">
              <Typography variant="h4">Formula Sheets</Typography>
            </Box>
          </Grid>

          <Grid item xs={12}>
            <StyledPaper>
              <Typography variant="body1" sx={{ mb: 2 }}>
                Access important formulas and their detailed breakdowns. Click a formula to expand its details.
              </Typography>

              {/* Display Formula Names and Expandable Info */}
              <Grid container spacing={2}>
                {formulasData.formulas.map((formula, index) => (
                  <Grid item xs={12} sm={6} md={4} key={index}>
                    <FeatureCard 
                      title={formula.name}
                      formula={formula.formula}
                      breakdown={formula.breakdown}
                      example={formula.example}
                      isSelected={selectedFormula === formula.name}
                      onClick={() => handleFormulaClick(formula.name)}
                    />
                  </Grid>
                ))}
              </Grid>
            </StyledPaper>
          </Grid>
        </Grid>
      </Box>
    </MathJaxContext>
  );
};

// Styling for the main paper that holds the formula content
const StyledPaper = styled(Paper)({
  padding: '20px',
  marginBottom: '20px',
  backgroundColor: '#333',
  color: '#fff',
  borderRadius: '8px',
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
});

// FeatureCard Component for individual formulas
const FeatureCard = ({ title, formula, breakdown, example, isSelected, onClick }) => {
  return (
    <StyledFeatureCard onClick={onClick}>
      <Typography variant="h6" sx={{ mb: 1 }}>
        {title}
      </Typography>
      {isSelected && (
        <Box sx={{ mt: 1 }}>
          <Typography variant="body1">
            Formula: <MathJax>{`\\( ${formula} \\)`}</MathJax>
          </Typography>
          <Typography variant="body1" sx={{ mt: 1 }}>
            <strong>Breakdown:</strong>
            {Object.entries(breakdown).map(([key, value]) => (
              <Typography key={key}>
                <strong>{key}:</strong> {value}
              </Typography>
            ))}
          </Typography>
          <Typography variant="body1" sx={{ mt: 1 }}>
            <strong>Example Problem:</strong> {example.problem}
          </Typography>
          <Typography variant="body1">
            <strong>Solution:</strong>
            {example.solution.map((step, i) => (
              <Typography key={i}>{step}</Typography>
            ))}
          </Typography>
        </Box>
      )}
    </StyledFeatureCard>
  );
};

// Styling for individual formula cards (similar to StudyMaterials)
const StyledFeatureCard = styled(Paper)({
  padding: '15px',
  backgroundColor: '#444',
  color: '#fff',
  borderRadius: '8px',
  textAlign: 'center',
  position: 'relative',
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
  height: 'auto',
  cursor: 'pointer',
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'center',
  '&:hover': {
    boxShadow: '0 6px 12px rgba(0, 0, 0, 0.3)',
  },
});

export default FormulaSheets;
