// src/App.js
import React, { useEffect, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation } from 'react-router-dom';
import { Box } from '@mui/material';
import Sidebar from './components/Sidebar';
import Dashboard from './components/Dashboard';
import TutoringHistory from './components/TutoringHistory';
import UpcomingSessions from './components/UpcomingSessions';
import StudyMaterials from './components/StudyMaterials';
import Settings from './components/Settings';
import PersonalDetails from './components/PersonalDetails';
import Notes from './components/Notes';
import AIQuiz from './components/AIQuiz';
import PastPapers from './components/PastPapers';
import Flashcards from './components/Flashcards';
import FormulaSheets from './components/FormulaSheets';
import PaymentSession from './components/PaymentSession';
import AdminDashboard from './components/AdminDashboard';
import AdminLogin from './components/AdminLogin';
import StudentLogin from './components/StudentLogin';
import ProtectedRoute from './components/ProtectedRoute';
import AdminProtectedRoute from './components/AdminProtectedRoute';
import { AuthProvider, useAuth } from './contexts/AuthContext';
import { StudentProvider } from './contexts/StudentContext';

function App() {
  return (
    <AuthProvider>
      <StudentProvider>
        <Router>
          <MainLayout />
        </Router>
      </StudentProvider>
    </AuthProvider>
  );
}

const MainLayout = () => {
  const { logout } = useAuth(); // Get the logout function from AuthContext
  const location = useLocation();
  const inactivityTimeout = useRef(null); // Ref to store timeout ID (to avoid resetting state on each rerender)
  const INACTIVITY_TIME = 15 * 60 * 1000; // 15 minutes

  // Define routes where the sidebar should be displayed
  const sidebarRoutes = [
    '/dashboard',
    '/tutoring-history',
    '/upcoming-sessions',
    '/study-materials',
    '/settings',
  ];

  // Check if the current route matches the sidebarRoutes array
  const shouldShowSidebar = sidebarRoutes.includes(location.pathname);

  // Function to reset the inactivity timer
  const resetTimer = () => {
    if (inactivityTimeout.current) {
      clearTimeout(inactivityTimeout.current);
    }

    // Set the inactivity timer
    inactivityTimeout.current = setTimeout(() => {
      logout(); // Log the user out after inactivity
      alert('You have been logged out due to inactivity.');
    }, INACTIVITY_TIME);
  };

  useEffect(() => {
    // List of events to track activity across devices
    const events = [
      'mousemove',  // Mouse move
      'keydown',    // Key press
      'click',      // Mouse click
      'scroll',     // Scroll event
      'touchstart', // Touch event start (mobile)
      'touchmove',  // Touch move (mobile)
      'wheel',      // Mouse wheel scroll (laptop/desktop)
      'pointermove', // Pointer move (works for both mouse and touch devices)
      'pointerdown', // Pointer down (mouse click, touch tap)
    ];

    // Add event listeners for each event to reset the timer
    events.forEach((event) => window.addEventListener(event, resetTimer));

    // Initialize timer when the component is mounted
    resetTimer();

    // Cleanup event listeners and timeout on unmount
    return () => {
      if (inactivityTimeout.current) {
        clearTimeout(inactivityTimeout.current);
      }
      events.forEach((event) => window.removeEventListener(event, resetTimer));
    };
  }, [resetTimer]); // Include resetTimer in the dependency array


  return (
    <div style={{ display: 'flex', minHeight: '100vh' }}>
      {shouldShowSidebar && <Sidebar />} {/* Conditionally render the sidebar */}
      <Box component="main" sx={{ flexGrow: 1, p: 3, backgroundColor: '#2c2c2c', color: '#fff' }}>
        <Routes>
          {/* Admin and Student Routes */}
          <Route path="/admin-login" element={<AdminLogin />} />
          <Route path="/admin-dashboard" element={<AdminProtectedRoute><AdminDashboard /></AdminProtectedRoute>} />
          <Route path="/student-login" element={<StudentLogin />} />

          {/* Protected Routes for Students */}
          <Route path="/dashboard" element={<ProtectedRoute><Dashboard /></ProtectedRoute>} />
          <Route path="/tutoring-history" element={<ProtectedRoute><TutoringHistory /></ProtectedRoute>} />
          <Route path="/upcoming-sessions" element={<ProtectedRoute><UpcomingSessions /></ProtectedRoute>} />
          <Route path="/study-materials" element={<ProtectedRoute><StudyMaterials /></ProtectedRoute>} />
          <Route path="/settings" element={<ProtectedRoute><Settings /></ProtectedRoute>} />
          <Route path="/personal-details" element={<ProtectedRoute><PersonalDetails /></ProtectedRoute>} />
          <Route path="/notes" element={<ProtectedRoute><Notes /></ProtectedRoute>} />
          <Route path="/ai-quiz" element={<ProtectedRoute><AIQuiz /></ProtectedRoute>} />
          <Route path="/past-papers" element={<ProtectedRoute><PastPapers /></ProtectedRoute>} />
          <Route path="/flashcards" element={<ProtectedRoute><Flashcards /></ProtectedRoute>} />
          <Route path="/formula-sheets" element={<ProtectedRoute><FormulaSheets /></ProtectedRoute>} />
          <Route path="/payment-session" element={<ProtectedRoute><PaymentSession /></ProtectedRoute>} />

          {/* Default Route Redirects to Student Login */}
          <Route path="/" element={<Navigate to="/student-login" />} />
        </Routes>
      </Box>
    </div>
  );
};

export default App;
