// src/components/AIQuiz.js
import React, { useState } from 'react';
import { Box, Typography, Button, List, ListItem, ListItemText, Radio, RadioGroup, FormControlLabel, FormControl, CircularProgress } from '@mui/material';
import quizQuestions from '../data/quizQuestions.json'; // Import the JSON file

const AIQuiz = () => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState('');
  const [score, setScore] = useState(0);
  const [showResult, setShowResult] = useState(false);

  const currentQuestion = quizQuestions[currentQuestionIndex];

  const handleAnswerChange = (event) => {
    setSelectedAnswer(event.target.value);
  };

  const handleNextQuestion = () => {
    if (selectedAnswer === currentQuestion.answer) {
      setScore(score + 1);
    }

    setSelectedAnswer('');
    if (currentQuestionIndex < quizQuestions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      setShowResult(true);
    }
  };

  const handleRestartQuiz = () => {
    setCurrentQuestionIndex(0);
    setScore(0);
    setShowResult(false);
    setSelectedAnswer('');
  };

  return (
    <Box
      sx={{
        flexGrow: 1,
        p: 3,
        backgroundColor: '#2c2c2c',
        color: '#fff',
        minHeight: '100vh',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
      }}
    >
      <Typography variant="h4" gutterBottom>
        AI Quiz
      </Typography>

      {!showResult ? (
        <>
          <Typography variant="h6" gutterBottom>
            {currentQuestion.question}
          </Typography>
          <FormControl component="fieldset">
            <RadioGroup value={selectedAnswer} onChange={handleAnswerChange}>
              {currentQuestion.options.map((option, index) => (
                <FormControlLabel
                  key={index}
                  value={option}
                  control={<Radio />}
                  label={option}
                />
              ))}
            </RadioGroup>
          </FormControl>
          <Button
            variant="contained"
            color="primary"
            onClick={handleNextQuestion}
            disabled={!selectedAnswer}
            sx={{ mt: 2 }}
          >
            {currentQuestionIndex < quizQuestions.length - 1 ? 'Next' : 'Finish'}
          </Button>
        </>
      ) : (
        <Box sx={{ textAlign: 'center' }}>
          <Typography variant="h5" gutterBottom>
            Quiz Completed!
          </Typography>
          <Typography variant="h6">
            Your Score: {score} / {quizQuestions.length}
          </Typography>
          <Button variant="contained" color="primary" onClick={handleRestartQuiz} sx={{ mt: 2 }}>
            Restart Quiz
          </Button>
        </Box>
      )}
    </Box>
  );
};

export default AIQuiz;
