// src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { Box } from '@mui/material'; // Import Box from Material-UI
import Sidebar from './components/Sidebar';
import Dashboard from './components/Dashboard';
import TutoringHistory from './components/TutoringHistory';
import UpcomingSessions from './components/UpcomingSessions';
import StudyMaterials from './components/StudyMaterials';
import Settings from './components/Settings';
import PersonalDetails from './components/PersonalDetails'; // Import the PersonalDetails component
import Notes from './components/Notes'; // Create this component
import AIQuiz from './components/AIQuiz'; // AI Quiz component
import PastPapers from './components/PastPapers'; // Create this component
import Flashcards from './components/Flashcards'; // Create this component
import FormulaSheets from './components/FormulaSheets'; // Create this c
import PaymentSession from './components/PaymentSession';
import AdminDashboard from './components/AdminDashboard';
import AdminLogin from './components/AdminLogin';
import PasswordReset from './components/PasswordReset'; // Password reset component

function App() {
  return (
    <Router>
      <div style={{ display: 'flex', minHeight: '100vh' }}>
        {/* Render Sidebar only once here */}
        <Sidebar />
        <Box component="main" sx={{ flexGrow: 1, p: 3, backgroundColor: '#2c2c2c', color: '#fff' }}>
          <Routes>
            <Route path="/student-dashboard" element={<StudentDashboard />} />
            <Route path="/admin-dashboard" element={<AdminDashboard />} />

            <Route path="/password-reset" element={<PasswordReset />} />
            <Route path="/dashboard" element={<Dashboard />} />
            <Route path="/tutoring-history" element={<TutoringHistory />} />
            <Route path="/upcoming-sessions" element={<UpcomingSessions />} />
            <Route path="/study-materials" element={<StudyMaterials />} />
            <Route path="/settings" element={<Settings />} />
            <Route path="/personal-details" element={<PersonalDetails />} /> {/* Define the route for Personal Details */}
            <Route path="/notes" element={<Notes />} />
            <Route path="/ai-quiz" element={<AIQuiz />} />
            <Route path="/past-papers" element={<PastPapers />} />
            <Route path="/flashcards" element={<Flashcards />} />
            <Route path="/formula-sheets" element={<FormulaSheets />} />
            <Route path="/Payment-Session" element={<PaymentSession />} />
            <Route path="/" element={<Dashboard />} />
          </Routes>
        </Box>
      </div>
    </Router>
  );
}

export default App;
