// src/components/Dashboard.js
import React from 'react';
import { Box, Grid, Paper, Typography, Avatar, IconButton, Divider, List, ListItem, ListItemIcon, ListItemText, Badge } from '@mui/material';
import NotificationsIcon from '@mui/icons-material/Notifications';
import ChatBubbleOutlineIcon from '@mui/icons-material/ChatBubbleOutline';
import EventIcon from '@mui/icons-material/Event';
import FlashOnIcon from '@mui/icons-material/FlashOn';
import AccountBalanceWalletIcon from '@mui/icons-material/AccountBalanceWallet';
import SchoolIcon from '@mui/icons-material/School';
import { styled } from '@mui/material/styles';
import student from '../data/student'; // Importing student data

const Dashboard = () => {
  // Extracting personal details, payment info, and upcoming sessions from student data
  const { personalDetails, paymentInformation, tutoringHistory } = student;

  // Assuming the last payment covers the latest sessions
  const latestPayment = paymentInformation[paymentInformation.length - 1];
  const balance = latestPayment ? latestPayment.amountPaid : 0;
  const sessionsCovered = latestPayment ? latestPayment.sessionsCovered : [];

  // Display the latest session type and dates
  const latestSession = sessionsCovered[0] || {};
  const sessionType = latestSession.subject || 'N/A';
  const startDate = latestSession.startDate || 'N/A';
  const endDate = latestSession.endDate || 'N/A';

  // Extract upcoming sessions from tutoring history, filter by future dates
  const upcomingSessions = tutoringHistory.filter(session => new Date(session.date) > new Date());

  return (
    <Box component="main" sx={{ flexGrow: 1, p: 3, backgroundColor: '#2c2c2c', color: '#ffffff', minHeight: '100vh' }}>
      <Grid container spacing={2}>
        <Grid item xs={12}>
          <Box display="flex" justifyContent="space-between" alignItems="center">
            <Typography variant="h4" sx={{ color: '#ffffff' }}>Hello, {personalDetails.name}!</Typography>
            <Box>
              <IconButton color="primary">
                <Badge badgeContent={4} color="error">
                  <NotificationsIcon sx={{ color: 'teal' }} />
                </Badge>
              </IconButton>
              <Avatar alt="Student Profile" src="/static/images/avatar/1.jpg" />
            </Box>
          </Box>
        </Grid>

        {/* Balance and Session Details */}
        <Grid item xs={12}>
          <StyledPaper>
            <Typography variant="h6" sx={{ display: 'flex', alignItems: 'center', color: '#ffffff' }}>
              <AccountBalanceWalletIcon sx={{ mr: 1, color: 'teal' }} /> Your Balance: R{balance}
            </Typography>
            <Typography variant="body1" sx={{ color: '#ffffff', mt: 1 }}>
              Session Type: {sessionType}
            </Typography>
            <Typography variant="body1" sx={{ color: '#ffffff' }}>
              Start Date: {startDate}
            </Typography>
            <Typography variant="body1" sx={{ color: '#ffffff' }}>
              End Date: {endDate}
            </Typography>
          </StyledPaper>
        </Grid>

        {/* Active Progress Section */}
        <Grid item xs={12} md={6}>
          <StyledPaper>
            <Typography variant="h6" sx={{ display: 'flex', alignItems: 'center', color: '#ffffff' }}>
              <FlashOnIcon sx={{ mr: 1, color: 'teal' }} /> Active Progress
            </Typography>
            <List>
              <ListItem>
                <ListItemIcon>
                  <FlashOnIcon sx={{ color: 'teal' }} />
                </ListItemIcon>
                <ListItemText primary="Flashcards: 75% Completed" primaryTypographyProps={{ color: '#ffffff' }} />
              </ListItem>
              <ListItem>
                <ListItemIcon>
                  <SchoolIcon sx={{ color: 'teal' }} />
                </ListItemIcon>
                <ListItemText primary="Notes Reviewed: 50%" primaryTypographyProps={{ color: '#ffffff' }} />
              </ListItem>
              <ListItem>
                <ListItemIcon>
                  <SchoolIcon sx={{ color: 'teal' }} />
                </ListItemIcon>
                <ListItemText primary="Quizzes: 2 out of 5 completed" primaryTypographyProps={{ color: '#ffffff' }} />
              </ListItem>
            </List>
          </StyledPaper>
        </Grid>

        {/* Upcoming Sessions Section */}
        <Grid item xs={12} md={6}>
          <StyledPaper>
            <Typography variant="h6" sx={{ display: 'flex', alignItems: 'center', color: '#ffffff' }}>
              <EventIcon sx={{ mr: 1, color: 'teal' }} /> Upcoming Sessions
            </Typography>
            <List>
              {upcomingSessions.length > 0 ? (
                upcomingSessions.map((session) => (
                  <ListItem key={session.id}>
                    <ListItemText
                      primary={`${session.subject} - ${session.topic}`}
                      secondary={`Date: ${new Date(session.date).toLocaleDateString()}`}
                      primaryTypographyProps={{ color: '#ffffff' }}
                      secondaryTypographyProps={{ color: '#ffffff' }}
                    />
                  </ListItem>
                ))
              ) : (
                <Typography sx={{ color: '#ffffff' }}>No upcoming sessions.</Typography>
              )}
            </List>
          </StyledPaper>
        </Grid>

        {/* Messaging Section */}
        <Grid item xs={12}>
          <StyledPaper>
            <Typography variant="h6" sx={{ display: 'flex', alignItems: 'center', color: '#ffffff' }}>
              <ChatBubbleOutlineIcon sx={{ mr: 1, color: 'teal' }} /> Messages
            </Typography>
            <Divider sx={{ my: 1 }} />
            <List>
              <ListItem>
                <ListItemText
                  primary="John Doe"
                  secondary="Hi, can you help me with my assignment?"
                  primaryTypographyProps={{ color: '#ffffff' }}
                  secondaryTypographyProps={{ color: '#ffffff' }}
                />
              </ListItem>
              <ListItem>
                <ListItemText
                  primary="Tutor Anna"
                  secondary="Don't forget your session tomorrow at 10 AM!"
                  primaryTypographyProps={{ color: '#ffffff' }}
                  secondaryTypographyProps={{ color: '#ffffff' }}
                />
              </ListItem>
            </List>
          </StyledPaper>
        </Grid>

        {/* Additional Section: Alerts or Reminders */}
        <Grid item xs={12}>
          <StyledPaper>
          <Typography variant="h6" sx={{ display: 'flex', alignItems: 'center', color: '#ffffff' }}>
              <NotificationsIcon sx={{ mr: 1, color: 'teal' }} /> Alerts & Reminders
            </Typography>
            <List>
              <ListItem>
                <ListItemText
                  primary="Assignment due: History Essay - Sep 12"
                  primaryTypographyProps={{ color: '#ffffff' }}
                />
              </ListItem>
              <ListItem>
                <ListItemText
                  primary="New Quiz Available: Science Chapter 4"
                  primaryTypographyProps={{ color: '#ffffff' }}
                />
              </ListItem>
            </List>
          </StyledPaper>
        </Grid>
      </Grid>
    </Box>
  );
};

const StyledPaper = styled(Paper)({
  padding: '20px',
  marginBottom: '20px',
  backgroundColor: '#333',
  color: '#ffffff',
  borderRadius: '8px',
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
});

export default Dashboard;
