import React, { useEffect, useState } from 'react';
import { Box, Grid, Paper, Typography, Avatar, Button, List, ListItem, ListItemText, Divider } from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import { styled } from '@mui/material/styles';
import dayjs from 'dayjs'; // For date comparisons
import { useStudent } from '../contexts/StudentContext'; // Import the student context

const StudentProfile = () => {
  const { currentStudent } = useStudent(); // Use context to get the current student
  const [student, setStudent] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Load student data dynamically based on the current student's ID
    const loadStudentData = async () => {
      if (currentStudent && currentStudent.id) {
        try {
          const data = await import(`../data/students/${currentStudent.id}.js`); // Dynamically import student data
          setStudent(data.default); // Set the student data
        } catch (err) {
          console.error('Error loading student data:', err);
          setError('Failed to load student data.');
        }
      } else {
        setError('No current student data available.');
      }
    };

    loadStudentData();
  }, [currentStudent]);

  if (error) {
    return <Typography sx={{ color: '#ffffff', textAlign: 'center', marginTop: '20px' }}>{error}</Typography>;
  }

  if (!student) {
    return <Typography sx={{ color: '#ffffff', textAlign: 'center', marginTop: '20px' }}>Loading...</Typography>;
  }

  // Destructure student data with fallback for missing info
  const {
    profilePicture,
    fullName = 'N/A',
    grade = 'N/A',
    studentId = 'N/A',
    dob = 'N/A',
    email = 'N/A',
    phone = 'N/A',
    address = 'N/A',
    paymentInformation = [],
    academicPerformance = {},
  } = student;

  const { averageGrade = 'N/A', bestSubject = 'N/A', subjectNeedingImprovement = 'N/A' } = academicPerformance;

  // Determine the current session based on today's date
  const today = dayjs();
  const currentSession = paymentInformation.find(
    (session) => today.isAfter(dayjs(session.startDate)) && today.isBefore(dayjs(session.endDate))
  );

  return (
    <Box sx={{ p: 3, backgroundColor: '#2c2c2c', color: '#ffffff', minHeight: '100vh' }}>
      {/* Header Section */}
      <StyledPaper>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={12} md={4}>
            <Avatar alt={fullName} src={profilePicture} sx={{ width: 120, height: 120, mb: 2 }} />
          </Grid>
          <Grid item xs={12} md={8}>
            <Typography variant="h4">{fullName}</Typography>
            <Typography variant="h6">Grade: {grade}</Typography>
            <Typography variant="body1">Student ID: {studentId}</Typography>
            <Button variant="contained" startIcon={<EditIcon />} sx={{ mt: 2, backgroundColor: 'teal' }}>
              Edit Profile
            </Button>
          </Grid>
        </Grid>
      </StyledPaper>

      {/* Personal Information Section */}
      <StyledPaper sx={{ mt: 4 }}>
        <Typography variant="h5" sx={{ mb: 2 }}>Personal Information</Typography>
        <List>
          <ListItem>
            <ListItemText primary="Full Name" secondary={fullName} />
          </ListItem>
          <ListItem>
            <ListItemText primary="Date of Birth" secondary={dob} />
          </ListItem>
          <ListItem>
            <ListItemText primary="Email Address" secondary={email} />
          </ListItem>
          <ListItem>
            <ListItemText primary="Phone Number" secondary={phone} />
          </ListItem>
          <ListItem>
            <ListItemText primary="Home Address" secondary={address || 'N/A'} />
          </ListItem>
        </List>
      </StyledPaper>

      {/* Current Session Section */}
      <StyledPaper sx={{ mt: 4 }}>
        <Typography variant="h5" sx={{ mb: 2 }}>Current Session</Typography>
        {currentSession ? (
          <List>
            <ListItem>
              <ListItemText primary="Session Type" secondary={currentSession.sessionType} />
            </ListItem>
            <ListItem>
              <ListItemText primary="Start Date" secondary={dayjs(currentSession.startDate).format('YYYY-MM-DD')} />
            </ListItem>
            <ListItem>
              <ListItemText primary="End Date" secondary={dayjs(currentSession.endDate).format('YYYY-MM-DD')} />
            </ListItem>
          </List>
        ) : (
          <Typography variant="body1" sx={{ color: '#bbb' }}>
            No active session
          </Typography>
        )}
      </StyledPaper>

      {/* Payment History */}
      <StyledPaper sx={{ mt: 4 }}>
        <Typography variant="h5" sx={{ mb: 2 }}>Payment History</Typography>
        <List>
          {paymentInformation.length > 0 ? (
            paymentInformation.map((payment, index) => (
              <Box key={index}>
                <ListItem>
                  <ListItemText
                    primary={`Payment ${index + 1}: R${payment.amountPaid}`}
                    secondary={`Date Paid: ${dayjs(payment.datePaid).format('YYYY-MM-DD')}`}
                    primaryTypographyProps={{ color: '#ffffff' }}
                    secondaryTypographyProps={{ color: '#cccccc' }}
                  />
                </ListItem>
                <Divider sx={{ backgroundColor: '#555', my: 1 }} />
              </Box>
            ))
          ) : (
            <Typography sx={{ color: '#ccc' }}>No payment history available.</Typography>
          )}
        </List>
      </StyledPaper>

      {/* Academic Insights Section */}
      <StyledPaper sx={{ mt: 4 }}>
        <Typography variant="h5" sx={{ mb: 2 }}>Academic Insights</Typography>
        <List>
          <ListItem>
            <ListItemText primary="Average Grade/Score" secondary={averageGrade} />
          </ListItem>
          <ListItem>
            <ListItemText primary="Best Performing Subject" secondary={bestSubject} />
          </ListItem>
          <ListItem>
            <ListItemText primary="Subject Needing Improvement" secondary={subjectNeedingImprovement} />
          </ListItem>
        </List>
      </StyledPaper>
    </Box>
  );
};

// Styled component for the Paper sections
const StyledPaper = styled(Paper)({
  padding: '20px',
  backgroundColor: '#333',
  color: '#ffffff',
  borderRadius: '12px',
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
  marginBottom: '20px',
});

export default StudentProfile;
