import React, { useEffect, useState } from 'react';
import { Box, Typography, Paper, List, ListItem, ListItemText, Divider, Slide, Fade } from '@mui/material';
import { styled } from '@mui/material/styles';
import AccountBalanceWalletIcon from '@mui/icons-material/AccountBalanceWallet';
import EventNoteIcon from '@mui/icons-material/EventNote';
import PaidIcon from '@mui/icons-material/Paid';
import HistoryIcon from '@mui/icons-material/History';
import { useStudent } from '../contexts/StudentContext'; // Import the custom hook

const PaymentSession = () => {
  const { currentStudent } = useStudent(); // Get the current student from context
  const [student, setStudent] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadStudentData = async () => {
      if (currentStudent && currentStudent.id) {
        try {
          const data = await import(`../data/students/${currentStudent.id}.js`);
          setStudent(data.default); // Set the student data
        } catch (err) {
          console.error('Error loading student data:', err);
          setError('Failed to load student data.');
        }
      } else {
        setError('No current student data available.');
      }
    };

    loadStudentData();
  }, [currentStudent]);

  // Show error if there's an issue loading the student data
  if (error) {
    return <Typography sx={{ color: '#ffffff', textAlign: 'center', marginTop: '20px' }}>{error}</Typography>;
  }

  // If data is still loading, show "Loading..."
  if (!student) {
    return <Typography sx={{ color: '#ffffff', textAlign: 'center', marginTop: '20px' }}>Loading...</Typography>;
  }

  const { paymentInformation = [] } = student;
  const latestPayment = paymentInformation.length > 0 ? paymentInformation[paymentInformation.length - 1] : null;
  const balance = latestPayment ? latestPayment.amountPaid : 0;
  const sessionsCovered = latestPayment ? latestPayment.sessionsCovered : [];
  const latestSession = sessionsCovered.length > 0 ? sessionsCovered[0] : {};
  const paymentStatus = latestPayment ? latestPayment.status || 'Pending' : 'Pending';

  return (
    <Box
      sx={{
        p: 3,
        backgroundColor: '#1e1e1e',
        color: '#ffffff',
        minHeight: '100vh',
        display: 'flex',
        justifyContent: 'center',
        overflowX: 'hidden', // Prevent horizontal scrollbars
      }}
    >
      <Slide direction="up" in={true} mountOnEnter unmountOnExit>
        <StyledPaper>
          <Fade in={true} timeout={1000}>
            <Box sx={{ textAlign: 'center', mb: 3 }}>
              <Typography variant="h4" sx={{ mb: 1, fontWeight: 'bold', color: 'teal' }}>
                Payment Session Overview
              </Typography>
              <Typography variant="body1" sx={{ color: '#bbb' }}>
                Here's your current payment status and session details.
              </Typography>
            </Box>
          </Fade>

          {/* Balance and Session Info */}
          <Box
            sx={{
              display: 'flex',
              justifyContent: 'space-between',
              flexWrap: 'nowrap', // Prevent stacking
              gap: { xs: 2, md: 4 }, // Increased spacing on mobile
              mb: 3,
              overflowX: 'auto', // Allow horizontal scroll on small screens
              scrollbarWidth: 'none', // Hide scrollbar in Firefox
              '&::-webkit-scrollbar': { display: 'none' }, // Hide scrollbar in Chrome
            }}
          >
            <InfoCard icon={<AccountBalanceWalletIcon sx={{ fontSize: 40, color: '#61dafb' }} />} label="Your Balance" value={`R${balance}`} />
            <InfoCard icon={<EventNoteIcon sx={{ fontSize: 40, color: '#f9a825' }} />} label="Session Type" value={latestSession.subject || 'N/A'} />
            <InfoCard icon={<PaidIcon sx={{ fontSize: 40, color: '#76ff03' }} />} label="Payment Status" value={paymentStatus} />
          </Box>

          {/* Session Period */}
          <Box
            sx={{
              display: 'flex',
              justifyContent: 'space-between',
              flexWrap: 'nowrap', // Prevent stacking
              gap: { xs: 2, md: 4 }, // Increased spacing on mobile
              mb: 3,
              overflowX: 'auto', // Allow horizontal scroll on small screens
              scrollbarWidth: 'none', // Hide scrollbar in Firefox
              '&::-webkit-scrollbar': { display: 'none' }, // Hide scrollbar in Chrome
            }}
          >
            <InfoCard icon={<EventNoteIcon sx={{ fontSize: 40, color: '#f50057' }} />} label="Start Date" value={latestSession.startDate || 'N/A'} />
            <InfoCard icon={<EventNoteIcon sx={{ fontSize: 40, color: '#00e676' }} />} label="End Date" value={latestSession.endDate || 'N/A'} />
          </Box>

          {/* Payment History */}
          <Typography variant="h6" sx={{ color: 'teal', mb: 2 }}>
            <HistoryIcon sx={{ verticalAlign: 'middle', mr: 1 }} /> Payment History
          </Typography>
          <List>
            {paymentInformation.length > 0 ? (
              paymentInformation.map((payment, index) => (
                <Fade in={true} timeout={800} key={index}>
                  <ListItem>
                    <ListItemText
                      primary={`Payment ${index + 1}: R${payment.amountPaid}`}
                      secondary={`Date: ${new Date(payment.datePaid).toLocaleDateString()}`}
                      primaryTypographyProps={{ color: '#ffffff', fontWeight: 'bold' }}
                      secondaryTypographyProps={{ color: '#cccccc' }}
                    />
                  </ListItem>
                </Fade>
              ))
            ) : (
              <Typography sx={{ color: '#ccc' }}>No payment history available.</Typography>
            )}
          </List>
        </StyledPaper>
      </Slide>
    </Box>
  );
};

// Styled component for the information card
const InfoCard = ({ icon, label, value }) => (
  <Box
    sx={{
      backgroundColor: '#333',
      padding: { xs: '15px', md: '20px' }, // Adjust padding based on screen size
      borderRadius: '12px',
      width: { xs: '100%', md: '30%' }, // Ensure the width remains consistent on mobile
      minWidth: { xs: '240px' }, // Minimum width for better mobile spacing
      textAlign: 'center',
      boxShadow: '0 4px 12px rgba(0, 0, 0, 0.3)',
      transition: 'transform 0.3s ease',
      '&:hover': {
        transform: 'scale(1.05)',
      },
    }}
  >
    {icon}
    <Typography variant="h6" sx={{ color: '#fff', mt: 1 }}>{label}</Typography>
    <Typography variant="body1" sx={{ color: '#ccc', mt: 1 }}>{value}</Typography>
  </Box>
);

const StyledPaper = styled(Paper)({
  padding: '30px',
  backgroundColor: '#2c2c2c',
  color: '#ffffff',
  borderRadius: '12px',
  boxShadow: '0 8px 16px rgba(0, 0, 0, 0.4)',
  maxWidth: '900px',
  width: '100%',
});

export default PaymentSession;
