// src/components/PaymentSession.js
import React from 'react';
import { Box, Typography, Paper, List, ListItem, ListItemText } from '@mui/material';
import { styled } from '@mui/material/styles';
import student from '../data/student'; // Importing student data

const PaymentSession = () => {
  const { paymentInformation } = student;
  const latestPayment = paymentInformation[paymentInformation.length - 1];
  const balance = latestPayment ? latestPayment.amountPaid : 0;
  const sessionsCovered = latestPayment ? latestPayment.sessionsCovered : [];
  const latestSession = sessionsCovered[0] || {};

  return (
    <Box sx={{ p: 3, backgroundColor: '#2c2c2c', color: '#ffffff', minHeight: '100vh' }}>
      <StyledPaper>
        <Typography variant="h4" sx={{ mb: 2 }}>
          Current Payment Session
        </Typography>
        <Typography variant="h6">Your Balance: R{balance}</Typography>
        <Typography variant="body1" sx={{ mt: 1 }}>
          Session Type: {latestSession.subject || 'N/A'}
        </Typography>
        <Typography variant="body1">Start Date: {latestSession.startDate || 'N/A'}</Typography>
        <Typography variant="body1">End Date: {latestSession.endDate || 'N/A'}</Typography>
        <Typography variant="body1">Payment Status: {latestPayment.status || 'Pending'}</Typography>
        <Typography variant="h6" sx={{ mt: 2 }}>
          Payment History
        </Typography>
        <List>
          {paymentInformation.map((payment, index) => (
            <ListItem key={index}>
              <ListItemText
                primary={`Payment ${index + 1}: R${payment.amountPaid}`}
                secondary={`Date: ${new Date(payment.date).toLocaleDateString()}`}
                primaryTypographyProps={{ color: '#ffffff' }}
                secondaryTypographyProps={{ color: '#ffffff' }}
              />
            </ListItem>
          ))}
        </List>
      </StyledPaper>
    </Box>
  );
};

const StyledPaper = styled(Paper)({
  padding: '20px',
  marginBottom: '20px',
  backgroundColor: '#333',
  color: '#ffffff',
  borderRadius: '8px',
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
});

export default PaymentSession;
