// src/components/AIQuiz.js
import React, { useState, useEffect } from 'react';
import { Box, Typography, Button, FormControl, MenuItem, Select, RadioGroup, FormControlLabel, Radio, List, ListItem, ListItemText } from '@mui/material';
import quizData from '../data/quizQuestions.json';
import student from '../data/student';

const AIQuiz = () => {
  const [selectedSubject, setSelectedSubject] = useState('');
  const [selectedTopic, setSelectedTopic] = useState('');
  const [numQuestions, setNumQuestions] = useState(5);
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState('');
  const [score, setScore] = useState(0);
  const [showResult, setShowResult] = useState(false);
  const [availableSubjects, setAvailableSubjects] = useState([]);
  const [availableTopics, setAvailableTopics] = useState([]);
  const [quizHistory, setQuizHistory] = useState([]);

  const studentGrade = `Grade ${student.personalDetails.grade}`;

  useEffect(() => {
    const subjects = Object.keys(quizData).filter(subject => 
      quizData[subject][studentGrade] !== undefined
    );
    setAvailableSubjects(subjects);
  }, [studentGrade]);

  useEffect(() => {
    if (selectedSubject) {
      const topics = Object.keys(quizData[selectedSubject][studentGrade]);
      setAvailableTopics(topics);
    }
  }, [selectedSubject, studentGrade]);

  const handleStartQuiz = () => {
    const selectedQuestions = quizData[selectedSubject][studentGrade][selectedTopic];
    const shuffledQuestions = selectedQuestions.sort(() => 0.5 - Math.random()).slice(0, numQuestions);
    setQuestions(shuffledQuestions);
    setCurrentQuestionIndex(0);
    setShowResult(false);
    setScore(0);
    setSelectedAnswer('');
  };

  const handleAnswerChange = (event) => {
    setSelectedAnswer(event.target.value);
  };

  const handleNextQuestion = () => {
    if (selectedAnswer === questions[currentQuestionIndex].answer) {
      setScore(score + 1);
    }
    setSelectedAnswer('');
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      setShowResult(true);
      saveQuizHistory();
    }
  };

  const saveQuizHistory = () => {
    const feedback = getFeedback(score, questions.length);
    const newHistory = {
      subject: selectedSubject,
      topic: selectedTopic,
      numQuestions: questions.length,
      score: score,
      feedback: feedback
    };
    setQuizHistory([...quizHistory, newHistory]);
  };

  const getFeedback = (score, total) => {
    const percentage = (score / total) * 100;
    if (percentage === 100) {
      return "Outstanding! You got everything correct!";
    } else if (percentage >= 80) {
      return "Great job! You're doing really well!";
    } else if (percentage >= 50) {
      return "Good effort! Keep practicing!";
    } else {
      return "Don't worry, keep trying and you'll improve!";
    }
  };

  const handleRestartQuiz = () => {
    setQuestions([]);
    setSelectedSubject('');
    setSelectedTopic('');
    setNumQuestions(5);
    setShowResult(false);
  };

  return (
    <Box
      sx={{
        flexGrow: 1,
        p: 3,
        backgroundColor: '#2c2c2c',
        color: '#fff',
        minHeight: '100vh',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
      }}
    >
      <Typography variant="h4" gutterBottom>
        AI Quiz for {student.personalDetails.name}
      </Typography>

      {!questions.length && !showResult && (
        <>
          <FormControl sx={{ mb: 2, minWidth: 120 }}>
            <Select 
              value={selectedSubject} 
              onChange={(e) => setSelectedSubject(e.target.value)} 
              displayEmpty
            >
              <MenuItem value="" disabled>Select Subject</MenuItem>
              {availableSubjects.map((subject) => (
                <MenuItem key={subject} value={subject}>{subject}</MenuItem>
              ))}
            </Select>
          </FormControl>

          {selectedSubject && (
            <FormControl sx={{ mb: 2, minWidth: 120 }}>
              <Select 
                value={selectedTopic} 
                onChange={(e) => setSelectedTopic(e.target.value)} 
                displayEmpty
              >
                <MenuItem value="" disabled>Select Topic</MenuItem>
                {availableTopics.map((topic) => (
                  <MenuItem key={topic} value={topic}>{topic}</MenuItem>
                ))}
              </Select>
            </FormControl>
          )}

          <FormControl sx={{ mb: 2, minWidth: 120 }}>
            <Select 
              value={numQuestions} 
              onChange={(e) => setNumQuestions(e.target.value)}
            >
              <MenuItem value={5}>5 Questions</MenuItem>
              <MenuItem value={10}>10 Questions</MenuItem>
            </Select>
          </FormControl>

          <Button 
            variant="contained" 
            color="primary" 
            onClick={handleStartQuiz} 
            disabled={!selectedTopic}
          >
            Start Quiz
          </Button>
        </>
      )}

      {questions.length > 0 && !showResult && (
        <>
          <Typography variant="h6" gutterBottom>
            {questions[currentQuestionIndex].question}
          </Typography>
          <FormControl component="fieldset">
            <RadioGroup value={selectedAnswer} onChange={handleAnswerChange}>
              {questions[currentQuestionIndex].options.map((option, index) => (
                <FormControlLabel
                  key={index}
                  value={option}
                  control={<Radio />}
                  label={option}
                />
              ))}
            </RadioGroup>
          </FormControl>
          <Button
            variant="contained"
            color="primary"
            onClick={handleNextQuestion}
            disabled={!selectedAnswer}
            sx={{ mt: 2 }}
          >
            {currentQuestionIndex < questions.length - 1 ? 'Next' : 'Finish'}
          </Button>
        </>
      )}

      {showResult && (
        <Box sx={{ textAlign: 'center' }}>
          <Typography variant="h5" gutterBottom>
            Quiz Completed!
          </Typography>
          <Typography variant="h6">
            Your Score: {score} / {questions.length}
          </Typography>
          <Typography variant="body1" sx={{ mt: 1 }}>
            {getFeedback(score, questions.length)}
          </Typography>
          <Button 
            variant="contained" 
            color="primary" 
            onClick={handleRestartQuiz} 
            sx={{ mt: 2 }}
          >
            Restart Quiz
          </Button>
        </Box>
      )}

      {quizHistory.length > 0 && (
        <Box sx={{ mt: 4, width: '100%', maxWidth: 600 }}>
          <Typography variant="h5" gutterBottom>
            Quiz History
          </Typography>
          <List>
            {quizHistory.map((history, index) => (
              <ListItem key={index}>
                <ListItemText 
                  primary={`Subject: ${history.subject}, Topic: ${history.topic}, Score: ${history.score}/${history.numQuestions}`} 
                  secondary={history.feedback} 
                />
              </ListItem>
            ))}
          </List>
        </Box>
      )}
    </Box>
  );
};

export default AIQuiz;
