// src/components/PasswordReset.js
import React, { useState } from 'react';
import { Box, Button, TextField, Typography, Paper } from '@mui/material';
import axios from 'axios'; // Import Axios for API requests

const PasswordReset = () => {
  const [studentId, setStudentId] = useState('');
  const [dateOfBirth, setDateOfBirth] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleResetPassword = async () => {
    if (newPassword !== confirmPassword) {
      setMessage('Passwords do not match.');
      return;
    }

    try {
      // Call backend API to reset password
      const response = await axios.put(`http://localhost:5000/api/students/${studentId}/reset-password`, {
        dateOfBirth,
        phoneNumber,
        newPassword,
      });

      setMessage('Password updated successfully.');
    } catch (error) {
      setMessage('Failed to reset password. Please check the entered details.');
    }
  };

  return (
    <Box display="flex" justifyContent="center" alignItems="center" height="100vh">
      <Paper sx={{ padding: '30px', width: '400px' }}>
        <Typography variant="h5" sx={{ marginBottom: '20px' }}>Reset Password</Typography>
        <TextField
          label="Student ID"
          fullWidth
          value={studentId}
          onChange={(e) => setStudentId(e.target.value)}
          sx={{ marginBottom: '15px' }}
        />
        <TextField
          label="Date of Birth"
          type="date"
          fullWidth
          value={dateOfBirth}
          onChange={(e) => setDateOfBirth(e.target.value)}
          InputLabelProps={{ shrink: true }}
          sx={{ marginBottom: '15px' }}
        />
        <TextField
          label="Phone Number"
          fullWidth
          value={phoneNumber}
          onChange={(e) => setPhoneNumber(e.target.value)}
          sx={{ marginBottom: '15px' }}
        />
        <TextField
          label="New Password"
          type="password"
          fullWidth
          value={newPassword}
          onChange={(e) => setNewPassword(e.target.value)}
          sx={{ marginBottom: '15px' }}
        />
        <TextField
          label="Confirm New Password"
          type="password"
          fullWidth
          value={confirmPassword}
          onChange={(e) => setConfirmPassword(e.target.value)}
          sx={{ marginBottom: '15px' }}
        />
        <Button variant="contained" color="primary" onClick={handleResetPassword} fullWidth>
          Reset Password
        </Button>
        {message && <Typography sx={{ marginTop: '15px', color: message.includes('successfully') ? 'green' : 'red' }}>{message}</Typography>}
      </Paper>
    </Box>
  );
};

export default PasswordReset;
