// src/components/UpcomingSessions.js
import React, { useState } from 'react';
import { Box, Grid, Typography, Paper } from '@mui/material';
import { styled } from '@mui/material/styles';
import { DateCalendar } from '@mui/x-date-pickers'; // Corrected import statement

const UpcomingSessions = () => {
  const [selectedDate, setSelectedDate] = useState(new Date());

  const handleDateChange = (date) => {
    setSelectedDate(date);
    // You can add functionality here to display or filter sessions based on the selected date
  };

  return (
    <Box component="main" sx={{ flexGrow: 1, p: 3, backgroundColor: '#2c2c2c', color: '#fff', minHeight: '100vh' }}>
      <Grid container spacing={2}>
        <Grid item xs={12}>
          <Box display="flex" justifyContent="space-between" alignItems="center">
            <Typography variant="h4">Upcoming Sessions</Typography>
          </Box>
        </Grid>

        <Grid item xs={12} md={6}>
          <StyledPaper>
            <Typography variant="h6">Your Upcoming Sessions</Typography>
            <Box mt={2}>
              <Typography variant="body1">Session 1: Database Systems - 08:30am - 10:00am</Typography>
              <Typography variant="body1">Session 2: UX/UI Design - 11:00am - 12:30pm</Typography>
              {/* Add more sessions as needed */}
            </Box>
          </StyledPaper>
        </Grid>

        <Grid item xs={12} md={6}>
          <StyledPaper>
            <Typography variant="h6">Select a Date</Typography>
            <Box mt={2}>
              <DateCalendar value={selectedDate} onChange={handleDateChange} />
            </Box>
          </StyledPaper>
        </Grid>
      </Grid>
    </Box>
  );
};

const StyledPaper = styled(Paper)({
  padding: '20px',
  marginBottom: '20px',
  backgroundColor: '#333',
  color: '#fff',
  borderRadius: '8px',
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
});

export default UpcomingSessions;
