// src/components/UpcomingSessions.js
import React, { useState, useEffect } from 'react';
import { Box, Grid, Typography, Paper } from '@mui/material';
import { styled } from '@mui/material/styles';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import interactionPlugin from '@fullcalendar/interaction';

const UpcomingSessions = () => {
  const [events, setEvents] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);
  const [slots, setSlots] = useState([]);

  useEffect(() => {
    // Fetch events data from your backend API and set them to the state
    fetch('/api/slots') // Adjust the endpoint based on your needs
      .then((response) => response.json())
      .then((data) => {
        const formattedEvents = data.map((slot) => ({
          title: slot.subjects ? slot.subjects.join(', ') : 'N/A',
          start: `${slot.date}T${convertTo24HourTime(slot.time)}`,
          color: slot.status === 'Available' ? '#9e9e9e' : '#004d40',
          textColor: '#ffffff',
        }));
        setEvents(formattedEvents);
      })
      .catch((error) => console.error('Error fetching events:', error));
  }, []);

  const handleDateClick = (info) => {
    setSelectedDate(info.dateStr);
    // Fetch and display slots for the selected date
    fetch(`/api/slots?date=${info.dateStr}`)
      .then((response) => response.json())
      .then((data) => setSlots(data))
      .catch((error) => console.error('Error fetching slots:', error));
  };

  return (
    <Box component="main" sx={{ flexGrow: 1, p: 3, backgroundColor: '#2c2c2c', color: '#fff', minHeight: '100vh' }}>
      <Grid container spacing={2}>
        <Grid item xs={12}>
          <Box display="flex" justifyContent="space-between" alignItems="center">
            <Typography variant="h4">Upcoming Sessions</Typography>
          </Box>
        </Grid>

        <Grid item xs={12} md={6}>
          <StyledPaper>
            <Typography variant="h6">Select a Date</Typography>
            <Box mt={2}>
              <FullCalendar
                plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
                initialView="dayGridMonth"
                headerToolbar={{
                  start: 'prev,next today',
                  center: 'title',
                  end: 'dayGridMonth,timeGridWeek',
                }}
                events={events}
                dateClick={handleDateClick}
                height="auto"
                contentHeight="auto"
              />
            </Box>
          </StyledPaper>
        </Grid>

        <Grid item xs={12} md={6}>
          <StyledPaper className="time-slots">
            <Typography variant="h6">Schedules for {selectedDate || "Selected Date"}</Typography>
            {slots.length === 0 ? (
              <Typography variant="body1">No schedules for this date.</Typography>
            ) : (
              slots.map((slot, index) => (
                <Box key={index} className={`slot ${slot.status === 'Available' ? 'available' : 'scheduled'}`} sx={{ mt: 2 }}>
                  <Typography variant="subtitle1">
                    {slot.subjects ? slot.subjects.join(', ') : 'N/A'}
                  </Typography>
                  <Typography variant="body2">
                    <strong>Time:</strong> {slot.time} - {slot.status}
                  </Typography>
                </Box>
              ))
            )}
          </StyledPaper>
        </Grid>
      </Grid>
    </Box>
  );
};

const StyledPaper = styled(Paper)({
  padding: '20px',
  marginBottom: '20px',
  backgroundColor: '#333',
  color: '#fff',
  borderRadius: '15px',
  boxShadow: '0 6px 18px rgba(0, 0, 0, 0.2)',
});

function convertTo24HourTime(time) {
  // Convert 'hh:mm AM/PM' to 'HH:MM' format for correct time display in the calendar
  const [timePart, modifier] = time.split(' ');
  let [hours, minutes] = timePart.split(':');
  if (hours === '12') {
    hours = '00';
  }
  if (modifier === 'PM') {
    hours = parseInt(hours, 10) + 12;
  }
  return `${hours}:${minutes}`;
}

export default UpcomingSessions;
